mapper.select:
SELECT COUNT(*)
FROM WORK
WHERE USER_ID = #{userId}
AND WORK_TYPE = 'WN'
AND GRADE_TYPE = #{gradeType}

SELECT *
FROM WORK W
JOIN ATTACHMENT A ON(W.WID = A.WID)
WHERE A.WID = #{wid}
AND W.GRADE_TYPE = #{gradeType}
AND A.ATTACHMENT_CATEGORY = 'ma'

mapper.update:
UPDATE WORK
SET WTITLE = #{wTitle},
    WINTRO = #{wIntro},
    WCYCLE = #{wCycle},
    GENRE = #{genre}
WHERE WID = #{wid}

UPDATE ATTACHMENT
SET ORIGIN_NAME = #{originName},
    CHANGE_NAME = #{changeName}
WHERE FID = #{fid}

UPDATE WORK
SET WORK_STATUS = #{workStatus}
WHERE WID = #{wid}

mapper.select:
SELECT COUNT(*)
FROM WORK_ROUND
WHERE WID = #{wid}

mapper.update:
UPDATE WORK_ROUND
SET RTITLE = #{rTitle},
    WR_COMMENT = #{wrComment},
    RCONTENT = #{rContent}
WHERE RID = #{rid}

mapper.delete:
DELETE FROM WORK 
WHERE WID = #{wid}
AND GRADE_TYPE = #{gradeType}

mapper.update:
UPDATE WORK 
SET VCOUNT=(SELECT VCOUNT FROM WORK WHERE WID=#{wid}) +1 
WHERE WID=#{wid}

mapper.select:
SELECT * 
FROM ATTENTION
WHERE WID = #{wid}
AND USER_ID = #{userId}

SELECT * 
FROM STARP
WHERE RID = #{rid}
AND USER_ID = #{userId}

SELECT TRUNC(AVG(STAR_POINT), 2)
FROM STARP
WHERE RID = #{rid}

SELECT COUNT(*)
FROM STARP
WHERE RID = #{rid}
