SET RTITLE = #{rTitle},
	WR_COMMENT = #{wrComment}
WHERE RID = #{rid}

UPDATE ATTACHMENT
SET ORIGIN_NAME = #{originName},
    CHANGE_NAME = #{changeName}
WHERE FID = #{fid}

UPDATE WORK 
SET WORK_STATUS = #{workStatus}
WHERE WID = #{wid}

UPDATE WORK
SET WORK_STATUS = 'REST'
WHERE WID = #{wid}

mapper.select:
