mapper.select:
SELECT COUNT(*)
FROM WORK_ROUND
WHERE WID = #{wid}

mapper.update:
UPDATE WORK
SET WTITLE = #{wTitle},
    WINTRO = #{wIntro},
    WCYCLE = #{wCycle},
    GENRE = #{genre}
WHERE WID = #{wid}

UPDATE ATTACHMENT
SET ORIGIN_NAME = #{originName},
    CHANGE_NAME = #{changeName}
WHERE FID = #{fid}
